mapper.select:
SELECT * FROM account A
INNER JOIN account_info AI ON A.account_no = AI.account_no

	A.account_id = #{accountId} AND A.account_pw = #{accountPw}

SELECT * FROM account A
INNER JOIN account_info AI ON A.account_no = AI.account_no

	A.account_name = #{name} AND AI.email = #{email}

mapper.update:
UPDATE account_info
SET account_gender = #{ gender }
	,email = #{ email }
	,phone = #{ phone }
	,post_num = #{ postNum }
	,address = #{ address }
	,address_detail = #{ addressDetail }
WHERE account_no = #{ accountNo }

mapper.select:
SELECT COUNT(*)
FROM account
WHERE account_id = #{ accountId } AND account_pw = #{ accountPw }

mapper.update:
UPDATE account
SET account_avail = 'N'
WHERE account_id = #{ accountId } AND account_pw = #{ accountPw }
