mapper.select:
SELECT n.notice_no, n.account_no, n.notice_title, n.notice_content, DATE_FORMAT(n.reg_date, '%Y-%m-%d') AS reg_date, DATE_FORMAT(n.mod_date, '%Y-%m-%d') AS mod_date, a.account_name AS writer, n.nail_up FROM notice n JOIN account a ON n.account_no = a.account_no
(n.notice_title LIKE CONCAT('%', #{keyword}, '%') OR a.account_name LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY CASE WHEN n.nail_up = 'Y' THEN 1 ELSE 0 END DESC, n.notice_no DESC LIMIT #{limitPageNo}, #{numPerPage}

mapper.update:
UPDATE notice
SET notice_title = #{noticeTitle},
    notice_content = #{noticeContent},
    nail_up = #{nailUp}
WHERE notice_no = #{noticeNo}
