mapper.select:
SELECT pet_no, account_no, pet_name, pet_gender, pet_age, pet_breed, save_name
FROM pet p
JOIN attachment a ON (p.pet_no = a.pk_no)
JOIN attach_type at ON (a.type_no = at.type_no)

	a.type_no = 2 AND account_no = #{accountNo}

LIMIT #{limitPageNo}, #{numPerPage}

mapper.update:
UPDATE pet
SET
pet_name = #{petName}
,pet_gender = #{petGender}
,pet_age = #{petAge}
,pet_breed = #{petBreed}

	account_no = #{accountNo}
	AND pet_no = #{petNo}

UPDATE attachment

   ori_name = #{originName}, 
   save_name = #{savedName}, 
  type_no = type_no


  type_no = #{typeNo}
  AND pk_no = #{pkNo}

mapper.delete:
DELETE a
FROM attachment a
JOIN (
	SELECT attach_no
	FROM attachment
	WHERE pk_no = #{petNo}
	) b
	ON a.attach_no = b.attach_no;

mapper.select:
SELECT *
FROM attachment

type_no = #{typeNo}
AND pk_no = #{pkNo}

SELECT save_name
FROM attachment

	type_no = #{typeNo}
	AND pk_no = #{pkNo}
