mapper.select:
SELECT r.*, a.account_id FROM review AS r JOIN ( SELECT c.class_no ,c.pet_no FROM class AS c ) AS c ON r.class_no = c.class_no JOIN ( SELECT p.pet_no ,p.account_no FROM pet AS p )AS p ON c.pet_no = p.pet_no JOIN ( SELECT a.account_no ,a.account_id FROM account AS a ) AS a ON p.account_no = a.account_no
review_title LIKE CONCAT('%' ,#{ keyword } ,'%')
ORDER BY r.review_no LIMIT #{ limitPageNo } ,#{ numPerPage }

mapper.update:
UPDATE review
SET review_title = #{ reviewTitle } ,review_content = #{ reviewContent }
WHERE review_no = #{ reviewNo }

mapper.select:
SELECT *
FROM attachment
WHERE type_no = 7 AND pk_no = #{ reviewNo }
